mapper.select:
SELECT Z.* FROM ( SELECT ROW_NUMBER() OVER(
${order.property} ${order.direction}
ORDER BY reg_dt DESC
) AS RNUM , X.* FROM (
sub_org_cd like #{search.agtCd} || '%'
AND ( p.pr_no like '%'||#{search.value}||'%' or p.ttl like '%'||#{search.value}||'%' or p.erp_pr_no like '%'||#{search.value}||'%' )
) X ) Z
WHERE Z.RNUM BETWEEN ( #{pageable.page} * #{pageable.size} + 1 ) AND ( (#{pageable.page} + 1) * #{pageable.size} )


 SELECT Max(a.pr_no) AS pr_no 
  FROM agt_rfqitem a 
WHERE a.rfq_no = #{rfqNo}


SELECT Max(a.pr_no) AS pr_no 
 FROM agt_poitem a 
 WHERE a.po_no = #{poNo}
