mapper.select:
SELECT Z.*
FROM (
  SELECT ROW_NUMBER() OVER(



                                                        ${order.property} ${order.direction}



                                                      ORDER BY reg_dt DESC


                                                ) AS RNUM
           ,  X.*
  FROM (





          evt.evt_cmd_param like '%'+#{search.value}+'%'




          AND evt.evt_io_type = #{search.evtIoType}




          AND evt.evt_st = #{search.evtSt}




          AND evt.agt_cd = #{search.agtCd}




  ) X
) Z


  WHERE Z.RNUM BETWEEN ( #{pageable.page} * #{pageable.size} + 1 ) AND ( (#{pageable.page} + 1) * #{pageable.size} )


SELECT count(*)
FROM agt_event
WHERE agt_cd = #{agtCd}

mapper.update:

UPDATE agt_event 
SET    reg_dt = CONVERT(CHAR(8), GETDATE(), 112) + REPLACE(CONVERT(CHAR(8), GETDATE(), 114), ':', ''), 
       req_cntn = #{reqCntn} 
WHERE  evt_no = #{evtNo}
