mapper.select:


WHERE qf.rfq_no = #{rfqNo}
    AND qf.quot_rev = #{quotRev}
    AND qf.cus_cd = #{cusCd}

mapper.insert:


			SELECT Substr(To_char(To_number('1' || Nvl(Max(qf.file_seq), '00000') + 1)), 2, 5) AS fileSeq 
              FROM agt_quotfile qf 
             WHERE qf.rfq_no = #{rfqNo} 
                  AND qf.quot_rev = #{quotRev} 
                  AND qf.cus_cd = #{cusCd} 



		INSERT INTO agt_quotfile (
            rfq_no, 
            quot_rev, 
            cus_cd, 
            file_seq, 
            file_no
        ) VALUES (
            #{rfqNo}, 
            #{quotRev}, 
            #{cusCd}, 
            #{fileSeq}, 
            #{fileNo}
        )
